package string;

public class Substring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s="sachintendulker";
		System.out.println("original string:"+ s);
		System.out.println("substring starts from index6 "  + s.substring(6));
		System.out.println("substring strat 0 and end 5: " + s.subSequence(0, 6));
	}

}
